const KeyboardKey = ({ skill, index, rowIndex }) => {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20, rotateX: -15 }}
      whileInView={{ opacity: 1, y: 0, rotateX: 0 }}
      viewport={{ once: true }}
      transition={{ delay: (rowIndex * 6 + index) * 0.05, duration: 0.4 }}
      whileHover={{
        y: -8,
        scale: 1.05,
        boxShadow: `0 20px 40px ${skill.color}40`,
      }}
      whileTap={{ y: 2, scale: 0.95 }}
      className="relative cursor-pointer group"
      style={{ perspective: "1000px" }}
    >
      {/* Key body */}
      <div
        className="relative w-20 h-20 md:w-24 md:h-24 rounded-xl flex items-center justify-center transition-all duration-300"
        style={{
          background: `linear-gradient(145deg, ${skill.color}, ${skill.color}cc)`,
          boxShadow: `
            0 6px 0 ${skill.color}88,
            0 8px 15px rgba(0,0,0,0.4),
            inset 0 1px 0 rgba(255,255,255,0.2),
            inset 0 -2px 0 rgba(0,0,0,0.2)
          `,
          transform: "translateY(0)",
        }}
      >
        {/* Icon or label */}
        <div
          style={{ color: skill.textColor }}
          className="transition-transform group-hover:scale-110"
        >
          {icons[skill.icon] || (
            <span className="text-lg md:text-xl font-bold">{skill.label}</span>
          )}
        </div>

        {/* Shine effect */}
        <div
          className="absolute inset-0 rounded-xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"
          style={{
            background:
              "linear-gradient(135deg, rgba(255,255,255,0.3) 0%, transparent 50%)",
          }}
        />
      </div>

      {/* Tooltip */}
      <div className="absolute -top-10 left-1/2 -translate-x-1/2 px-2 py-1 bg-white/10 backdrop-blur-md rounded text-xs text-white opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap pointer-events-none border border-white/10">
        {skill.id.charAt(0).toUpperCase() + skill.id.slice(1)}
      </div>
    </motion.div>
  );
};

const KeyboardSkills = () => {
  const { mode } = useMode();
  const isRobotics = mode === "robotics";
  const skillsData = isRobotics ? roboticsSkillsData : devSkillsData;

  return (
    <section
      id="skills"
      className="section-padding relative overflow-hidden bg-linear-to-b from-black via-neutral-950 to-black"
    >
      {/* Background decoration */}
      <div
        className={`absolute top-1/3 right-0 w-96 h-96 ${isRobotics ? "bg-blue-500/5" : "bg-neon-green/5"} rounded-full blur-3xl`}
      />
      <div
        className={`absolute bottom-1/3 left-0 w-96 h-96 ${isRobotics ? "bg-blue-500/5" : "bg-neon-green/5"} rounded-full blur-3xl`}
      />

      <div className="container-custom relative z-10">
        {/* Section Header */}
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.div
            className={`inline-flex items-center gap-2 px-4 py-2 rounded-full glass-card border ${isRobotics ? "border-blue-400/20 text-blue-400" : "border-neon-green/20 text-neon-green"} text-sm font-semibold mb-6`}
            animate={{ scale: [1, 1.05, 1] }}
            transition={{ duration: 2, repeat: Infinity }}
          >
            <Sparkles size={16} />
            Tech Stack
          </motion.div>
          <h2 className="text-5xl md:text-7xl font-extrabold mb-6">
            <span className="gradient-text">SKILLS</span>
          </h2>
          <p className="text-neutral-500 text-sm">(hint: hover over a key)</p>
        </motion.div>

        {/* Keyboard Container */}
        <motion.div
          className="max-w-2xl mx-auto"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <div
            className="p-6 md:p-10 rounded-3xl"
            style={{
              background: "linear-gradient(145deg, #1a1a1a, #0d0d0d)",
              boxShadow:
                "0 25px 50px -12px rgba(0, 0, 0, 0.8), inset 0 1px 0 rgba(255,255,255,0.05)",
            }}
          >
            {/* Keyboard rows */}
            <div className="flex flex-col gap-3 md:gap-4">
              {skillsData.map((row, rowIndex) => (
                <div
                  key={rowIndex}
                  className="flex justify-center gap-2 md:gap-3"
                  style={{
                    paddingLeft:
                      rowIndex === 1
                        ? "1rem"
                        : rowIndex === 2
                          ? "1.5rem"
                          : rowIndex === 3
                            ? "2rem"
                            : "0",
                  }}
                >
                  {row.map((skill, index) => (
                    <KeyboardKey
                      key={skill.id}
                      skill={skill}
                      index={index}
                      rowIndex={rowIndex}
                    />
                  ))}
                </div>
              ))}
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default KeyboardSkills;
